    if (alreadyChosenSpells) {
        return null;
    }
    alreadyChosenSpells = true;
    const spells = {
      a: 'thrust',
      q: 'difire',
      w: 'repulsor',
      e: 'saber',
      r: 'repeater',
      f: 'bump',
    }
    return { spells };
}

